mapper.update:
update car_pattern set

    car_pattern=#{carPattern}
    car_brand_id=#{carBrandId}
    time_pattern=#{timePattern}
    pattern_key=#{PatternKey}

where id=#{id}

mapper.select:
select count(*) from car_pattern
where car_brand_id=#{carBrandId} and car_pattern=#{carPattern} and time_pattern=#{timePattern} and pattern_key=#{patternKey} and id!=#{id}
limit 1
